import React, { Component } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

class Like extends Component {
  constructor() {
    super();
    this.state = { uni: ["far", "heart"] };
  }
  iconHandeler = () => {
    let uni;
    switch (this.state.uni[0]) {
      case "far":
        uni = ["fas", "heart"];
        break;
      case "fas":
        uni = ["far", "heart"];
    }

    this.setState({ uni });
  };
  render() {
    return (
      <td onClick={this.iconHandeler}>
        <FontAwesomeIcon icon={this.state.uni} style={{ cursor: "pointer" }} />
      </td>
    );
  }
}

export default Like;
