// so the target is to build a table

import React, { Component } from "react";
import { getMovies } from "../services/fakeMovieService";
import Like from "./like";

import Header1 from "./TR";

export default class Table1 extends Component {
  constructor() {
    super();
    this.state = this.state = { movies: getMovies() };
  }
  findItem(e) {
    let movie = e.target.parentNode.parentNode;

    let movi = this.state.movies.filter(m => m._id !== movie.id);
    this.setState({ movies: movi });
    console.log(movi);
  }
  IconChangeHandeler = movie => {
    console.log(movie);
    let uni = this.state.uni;
    const index = uni.indexOF(movie);
    uni[index] = movie;
    uni[index].like = !uni[index].like;
    console.log(uni[index].like);
  };

  render() {
    if (this.state.movies.length !== 0) {
      return (
        <div>
          <Header1 total={this.state.movies.length} />
          <table className="table">
            <thead>
              <tr>
                <th>Title</th>
                <th>Genre</th>
                <th>Stock</th>
                <th>Rate</th>
                <th>Icon</th>
                <th>Button</th>
              </tr>
            </thead>
            <tbody>
              {this.state.movies.map(movie => (
                <tr key={movie._id} id={movie._id}>
                  <td>{movie.title}</td>
                  <td>{movie.genre.name}</td>
                  <td>{movie.numberInStock}</td>
                  <td>{movie.dailyRentalRate}</td>

                  <Like movie={movie} onIconChange={this.IconChangeHandeler} />

                  <td>
                    <button
                      onClick={this.findItem.bind(this)}
                      type="button"
                      className="btn btn-danger btn-sm"
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      );
    } else {
      return (
        <div>
          <Header1 total={this.state.movies.length} />
        </div>
      );
    }
  }
}
