import React, { Component } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

class Like extends Component {
  render() {
    let lik;

    this.props.movie.like === "True" ? (lik = "fas") : (lik = "far");

    return (
      <td>
        <FontAwesomeIcon
          onClick={() => this.props.onIconChange(this.props.movie)}
          icon={[lik, "heart"]}
          style={{ cursor: "pointer" }}
        />
      </td>
    );
  }
}

export default Like;
