import React, { Component } from "react";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";

class Like extends Component {
  constructor() {
    super();
    this.state = { uni: ["far", "heart"] };
  }
  iconHandeler = () => {
    let uni;
    switch (this.state.uni[0]) {
      case "far":
        uni = "fas";
        break;
      case "fas":
        uni = "far";
    }
    this.setState([uni, "heart"]);
  };
  render() {
    return <FontAwesomeIcon onClick="iconHandeler" icon={["far", "heart"]} />;
  }
}

export default Like;
